import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [activeLink, setActiveLink] = useState('/');

  // Handle scroll event for sticky header
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    // Update active link based on current path
    setActiveLink(window.location.pathname);
  }, []);

  return (
    <header className={`header ${scrolled ? 'scrolled' : ''}`}>
      <div className="header-pattern"></div>
      <div className="header-decoration left"></div>
      <div className="header-decoration right"></div>
      
      <div className="container header-container">
        <Link to="/" className="logo" onClick={() => setActiveLink('/')}>
          <div className="logo-emblem">
            <span className="logo-letter">S</span>
          </div>
          <div className="logo-text">
            <h1>Sri Saravana <span className="highlight">Textiles</span></h1>
            <span className="tagline">Quality craftsmanship since 1995</span>
          </div>
        </Link>
        
        <button 
          className="mobile-menu-btn" 
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          aria-label="Toggle menu"
        >
          <div className={`hamburger ${isMenuOpen ? 'open' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </button>
        
        <nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <ul>
            <li className={activeLink === '/' ? 'active' : ''}>
              <Link to="/" onClick={() => {setIsMenuOpen(false); setActiveLink('/')}}>Home</Link>
            </li>
            <li className={activeLink === '/about' ? 'active' : ''}>
              <Link to="/about" onClick={() => {setIsMenuOpen(false); setActiveLink('/about')}}>About</Link>
            </li>
            <li className={activeLink === '/products' ? 'active' : ''}>
              <Link to="/products" onClick={() => {setIsMenuOpen(false); setActiveLink('/products')}}>Products</Link>
            </li>
            <li className={activeLink === '/contact' ? 'active' : ''}>
              <Link to="/contact" onClick={() => {setIsMenuOpen(false); setActiveLink('/contact')}}>Contact</Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
